                req.body.column,
                req.body.table_name,
                moment(req.body.date).format('L')
            ],
            (err, rows, fields) => {
                if (!err) {
                    res.attachment('filename.json');
                    res.set({
                        'Access-Control-Allow-Origin': '*'
                    }).csv(rows);
                } else
                    console.log(err);
            })
    })


}

